//Header luminosity----------------------------------------------------------/

    //Set luminosity, to change the logo and menu icon contrast
    function setLuminosity(luminosity) {

        //Define default property value
        luminosity = typeof luminosity !== 'undefined' ? luminosity : 'light';
        speed = $('body').is('.light, .dark') !== false ? 'fast' : 0;

        //If luminosity has changed, swap luminosity elements
        if ($('body').hasClass(luminosity) == false && luminosity !== "" && $('body').hasClass('hero-full') == true) {

            //Determine old luminosity
            oldluminosity = luminosity == 'light' ? 'dark' : 'light';

            //Change luminosity class on body
            $('body').removeClass('light dark').addClass(luminosity);

            //Cross-fade logo with appropriate image
            $('#logo').find('.' + oldluminosity).fadeOut(speed, function() {
                $('#logo').find('.' + luminosity).fadeIn(speed);
            });

            //Cross-fade menu opener
            $('#open_menubar').find('.' + oldluminosity).fadeOut(speed, function() {
                $('#open_menubar').find('.' + luminosity).fadeIn(speed).css('display', 'block');
            });    

            //Cross-fade pager color
            $('.bx-pager-link span').fadeOut(speed, function() {
                $('.bx-pager-link span').css('background-color', luminosity == 'light' ? '#58585A' : '#fff');
                $('.bx-pager-link span').fadeIn(speed);
            });   

            //Cross-fade pager arrows                     
            $('.freepager-next, .freepager-previous').fadeOut(speed, function() {
                $('.freepager-next, .freepager-previous').removeClass('light dark');
                $('.freepager-next, .freepager-previous').addClass(luminosity);
                $('.freepager-next, .freepager-previous').fadeIn(speed);
            });

        }

    }

    //Set initial slide luminosity if not using a slider
    if ($('.hero li').length == 1) {
        luminosity = $('.hero li:visible').find('.luminosity').text();
        setLuminosity($.trim(luminosity));
    }



//Fit images in carousel ---------------------------------------------------------------------/

    function fitImages(element) {
        
        $(element).each(function() {

            image = $(this).find('img');

            heightRatio = $(this).height() / $(image).attr('height');
            widthRatio = $(this).width() / $(image).attr('width');

            if (heightRatio > widthRatio) {
                $(image).width($(image).attr('width') * heightRatio);
                $(image).height($(image).attr('height') * heightRatio);                
            } 
            else {
                $(image).width($(image).attr('width') * widthRatio);
                $(image).height($(image).attr('height') * widthRatio); 
            }

        });


    }


